<script type="module">
$(document).click(function(event) {
    if (!$(event.target).closest('#search-box').length) {
        $('#search-recent').hide();
        $('#search-box').removeClass('px-4').addClass('w-[40px] h-[40px] rounded-full overflow-hidden flex items-center justify-center pl-2');
        $('#search-master').removeClass('shadow-lg rounded-md bg-white');

        if(window.innerWidth <= 1026){
            $('#search').addClass('hidden');
        }
    }

    if (!$(event.target).closest('#open-messager').length) {
        $('#messager').hide();
        $(this).removeClass('bg-blue-300');
    }

    if (!$(event.target).closest('#open-notifications').length) {
        $('#notifications').hide();
        $(this).removeClass('bg-blue-300');
    }

});


$('#search-box').click(function() {
    $(this).removeClass('!w-[40px] !h-[40px] rounded-full overflow-hidden pl-2').addClass('px-4');
    $('#search-recent').show();
    $('#search').removeClass('hidden');
    $('#search-master').addClass('shadow-lg rounded-md bg-white z-[999999]');
});

$('#open-messager').click(function() {
    $(this).toggleClass('bg-blue-300');
    $('#open-notifications').removeClass('bg-blue-300');
    $('#notifications').hide();
    $('#messager').toggle();
});

$('#open-sidebar').click(function() {
    $(this).toggleClass('bg-blue-300');
    $('#open-sidebar').removeClass('bg-blue-300');
    $('#sidebar').toggle();
    
});

$('#open-notifications').click(function() {
    $(this).toggleClass('bg-blue-300');
    $('#open-messager').removeClass('bg-blue-300');
    $('#messager').hide();
    $('#notifications').toggle();
});

$('.m-items').mouseenter(function() {
  $(this).find('.menu').show();
}).mouseleave(function() {
  $(this).find('.menu').hide();
});

</script>
